DECLARE @k INT = 1;
DECLARE @TotalRows INT = (SELECT COUNT(*) FROM #TempSachData);
WHILE @k <= @TotalRows
BEGIN
    -- Lấy dữ liệu từ bảng tạm
    SELECT 
        @TenSach = TenSach, 
        @MaTheLoai = MaTheLoai, 
        @MoTa = MoTa, 
        @NamXuatBan = NamXuatBan, 
        @GiaBia = GiaBia, 
        @SoTrang = SoTrang, 
        @DinhDang = DinhDang
    FROM #TempSachData WHERE STT = @k;

	-- Tạo ISBN giả
    SET @ISBN = 'ISBN' + RIGHT('0000000000' + CAST(100 + @k AS VARCHAR(10)), 10);

    SET @MaTacGia = (SELECT MaTacGia FROM TACGIA ORDER BY NEWID() OFFSET 0 ROWS FETCH NEXT 1 ROWS ONLY);
    SET @MaNXB = (SELECT MaNXB FROM NHAXUATBAN ORDER BY NEWID() OFFSET 0 ROWS FETCH NEXT 1 ROWS ONLY);

	SET @CreatedBy = (SELECT TOP 1 Email FROM @CreatorPool ORDER BY NEWID());
	IF @CreatedBy IS NULL
		SET @CreatedBy = @AdminEmail;

    -- CHÈN DỮ LIỆU
    INSERT INTO SACH (
        ISBN, TenSach, MaTacGia, MaTheLoai, NamXuatBan, DinhDang, MoTa, MaNXB, GiaBia, SoTrang, CreatedBy
    )
    VALUES (
        @ISBN, 
        @TenSach, 
        @MaTacGia, 
        @MaTheLoai, 
        @NamXuatBan, 
        @DinhDang, 
        @MoTa, 
        @MaNXB, 
		@GiaBia, 
		@SoTrang,
		@CreatedBy
    );

    SET @k = @k + 1;
END;
GO

-- Xóa bảng tạm 
DROP TABLE #TempSachData;
GO

SELECT ISBN, TenSach, TenTacGia, TenTheLoai, TenNXB
FROM SACH
JOIN TACGIA
ON SACH.MaTacGia = TACGIA.MaTacGia
JOIN THELOAI
ON SACH.MaTheLoai = THELOAI.MaTheLoai
JOIN NHAXUATBAN
ON SACH.MaNXB = NHAXUATBAN.MaNXB